import React, { Component } from 'react';
import CalcGrid from '../components/react-calculator/calc-grid';
import Calculator from './calculator';

class ReactCalculator extends Component{

    constructor(props){
        super(props);
        let id = this.props.id ? this.props.id : Math.random().toString(36).substring(7);
        this.state = {
            calc: new Calculator(),
            id: id,
        }
        console.log(this.state.id);
    }

    set inputValue(value){
        document.getElementById(this.state.id).value = value;
    }

    get inputValue(){
        return document.getElementById(this.state.id).value;
    }

    clear = (e, id) =>{
        this.inputValue = "";
        let calc = this.state.calc;
        calc.empty();
        this.setState({calc: calc});
    }


    setValue = (e, id) => {    
        let txtVal = document.getElementById(id);
        console.log(txtVal.value);
        if (this.state.showingResults){
            txtVal.value='';    
        }
        txtVal.value = txtVal.value + e.target.value;
    }


    calc = (a, b, operation) => {

        return a;
    }


    doOperation = (e, id) => {
        const txtVal = document.getElementById(id);
        const nmb = Number(txtVal.value);
        let total = this.state.total;
        total = this.calc(total, nmb, e.target.value === '='? this.state.operation : e.target.value);

        this.setState({
            total: total,
            showingResults: true,
            operation: e.target.value
        })
        txtVal.value = total;

    }


    render() {
        return (
            <div>
                <CalcGrid 
                    id={`${this.state.id}`}
                    clear={this.clear}
                    setValue={this.setValue}
                    doOperation={this.doOperation}
                    /> 
            </div>
        )
    }
}


export default ReactCalculator;
