class Calculator {

    constructor(){
        this.ops = [];
    }

    add = (number, operation) => {
        try {
            this.ops.push( {
                'number': Number(number),
                'operation': operation
            });
            return true;
        } catch (error) {
            return false;
        }
    }

    get operations(){
        return this.ops;
    }

    static get addition(){
        return "+";
    }

    static get substraction(){
        return '-';
    }

    static get multiplication(){
        return '*';
    }

    static get division(){
        return "/";
    }

}

export default Calculator;
