import React, { Component } from 'react';
import CalcButton from './CalcButton';


class Calculator extends Component{


    state = {
        total: 0,
        inputID: undefined,
        showingResults: false,
        operation:undefined
    }

    clear = (e, id) =>{
        let txtVal = document.getElementById(id);
        txtVal.value = "";
        this.setState({
            total: 0,
            showingResults: false
        })
    }


    setValue = (e, id) => {    
        let txtVal = document.getElementById(id);
        console.log(txtVal.value);
        if (this.state.showingResults){
            txtVal.value='';    
        }
        txtVal.value = txtVal.value + e.target.value;
    }


    calc = (a, b, operation) => {
        switch(operation){
            case '+':
                a += b; 
                break;
            case '-':
                a -= b; 
                break;
            case '*':
                a *= b; 
                break;
            case '/':
                a /= b; 
                break;
            case '=':
                a += b; 
                break;
            default:
                break;
        }
        return a;
    }


    doOperation = (e, id) => {
        const txtVal = document.getElementById(id);
        const nmb = Number(txtVal.value);
        let total = this.state.total;
        total = this.calc(total, nmb, e.target.value === '='? this.state.operation : e.target.value);

        this.setState({
            total: total,
            showingResults: true,
            operation: e.target.value
        })
        txtVal.value = total;

    }


    render() {
        return (
            <div className='calc-button-grid'> 
                <input className='calc-input calc-button-grid-item' type='text' id={this.props.calculatorId} />
                <CalcButton value='C' click= { (e) => this.clear(e, this.props.calculatorId) }  />
                <CalcButton value='+/-' click= { (e) => this.clear(e, this.props.calculatorId) }  />
                <CalcButton value='%' click= { (e) => this.doOperation(e, this.props.calculatorId) }  />
                <CalcButton value='/' click= { (e) => this.doOperation(e, this.props.calculatorId) }  />

                <CalcButton value='7' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='8' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='9' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='X' click={ (e) => this.doOperation(e, this.props.calculatorId) }  />

                <CalcButton value='4' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='5' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='6' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='-' click={ (e) => this.doOperation(e, this.props.calculatorId) }  />

                <CalcButton value='1' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='2' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='3' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='+' click={ (e) => this.doOperation(e, this.props.calculatorId) }  />

                <CalcButton value='0' click={ (e) => this.setValue(e, this.props.calculatorId) } gridClass='calc-button-grid-item-zero' />
                <CalcButton value='.' click={ (e) => this.setValue(e, this.props.calculatorId) }  />
                <CalcButton value='=' click={ (e) => this.doOperation(e, this.props.calculatorId) }  />

            </div>
        )
    }
}


export default Calculator;
