import React, { Component } from 'react';
import CalcGrid from '../components/react-calculator/calc-grid';


class Calculator extends Component{


    state = {
        total: 0,
        inputID: undefined,
        showingResults: false,
        operation:undefined
    }


    clear = (e, id) =>{
        let txtVal = document.getElementById(id);
        txtVal.value = "";
        this.setState({
            total: 0,
            showingResults: false
        })
    }


    setValue = (e, id) => {    
        let txtVal = document.getElementById(id);
        console.log(txtVal.value);
        if (this.state.showingResults){
            txtVal.value='';    
        }
        txtVal.value = txtVal.value + e.target.value;
    }


    calc = (a, b, operation) => {
        switch(operation){
            case '+':
                a += b; 
                break;
            case '-':
                a -= b; 
                break;
            case '*':
                a *= b; 
                break;
            case '/':
                a /= b; 
                break;
            case '=':
                a += b; 
                break;
            default:
                break;
        }
        return a;
    }


    doOperation = (e, id) => {
        const txtVal = document.getElementById(id);
        const nmb = Number(txtVal.value);
        let total = this.state.total;
        total = this.calc(total, nmb, e.target.value === '='? this.state.operation : e.target.value);

        this.setState({
            total: total,
            showingResults: true,
            operation: e.target.value
        })
        txtVal.value = total;

    }


    render() {
        return (
            <div>
                <CalcGrid /> 
            </div>
        )
    }
}


export default Calculator;
