import React, { Component } from 'react';
import CalcGrid from '../components/react-calculator/calc-grid';
import Calculator from './calculator';

class ReactCalculator extends Component{

    constructor(props){
        super(props);
        let id = this.props.id ? this.props.id : Math.random().toString(36).substring(7);
        this.state = {
            calc: new Calculator(),
            id: id,
        }
        console.log(this.state.id);
    }

    set inputValue(value){
        document.getElementById(this.state.id).value = value;
    }

    get inputValue(){
        return document.getElementById(this.state.id).value;
    }

    clear = (e) =>{
        this.inputValue = "";
        let calc = this.state.calc;
        calc.empty();
        this.setState({calc: calc});
    }

    setValue = (e) => {    
        this.inputValue += e.target.value;
    }

    doOperation = (e) => {
        let calc = this.state.calc;
        calc.add(this.inputValue, e.target.value);
        this.setState({calc: calc});
        this.inputValue = "";
    }


    render() {
        return (
            <div>
                <CalcGrid 
                    id={`${this.state.id}`}
                    clear={this.clear}
                    setValue={this.setValue}
                    doOperation={this.doOperation}
                    /> 
            </div>
        )
    }
}


export default ReactCalculator;
