import React from 'react';
import CalcButton from './CalcButton';


const clear = (e, id) =>{
    let txtVal = document.getElementById(id);
    txtVal.value = "";
}


const setValue = (e, id) => {    
    let txtVal = document.getElementById(id);
    console.log(txtVal.value);
    txtVal.value = txtVal.value + e.target.value;
}


const calculator = (props) => {
    return (
        <div className='calc-button-grid'> 
            <input type='text' id={props.calculatorId} style={{'gridColStart': 1,'gridColEnd': 3}} />
            <CalcButton value='C' click= { (e) => clear(e, props.calculatorId) }  />
            <CalcButton value='7' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='8' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='9' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='X' click={ (e) => clear(e, props.calculatorId) }  />
        </div>
    )
}


export default calculator;
