import React from 'react';
import CalcButton from './CalcButton';


const clear = (e, id) =>{
    let txtVal = document.getElementById(id);
    txtVal.value = "";
}


const setValue = (e, id) => {    
    let txtVal = document.getElementById(id);
    console.log(txtVal.value);
    txtVal.value = txtVal.value + e.target.value;
}


const calculator = (props) => {
    return (
        <div className='calc-button-grid'> 
            <input className='calc-input calc-button-grid-item' type='text' id={props.calculatorId} />
            <CalcButton value='C' click= { (e) => clear(e, props.calculatorId) }  />
            <CalcButton value='+/-' click= { (e) => clear(e, props.calculatorId) }  />
            <CalcButton value='%' click= { (e) => clear(e, props.calculatorId) }  />
            <CalcButton value='/' click= { (e) => clear(e, props.calculatorId) }  />

            <CalcButton value='7' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='8' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='9' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='X' click={ (e) => clear(e, props.calculatorId) }  />

            <CalcButton value='4' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='5' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='6' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='-' click={ (e) => clear(e, props.calculatorId) }  />

            <CalcButton value='1' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='2' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='3' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='+' click={ (e) => clear(e, props.calculatorId) }  />

            <CalcButton value='0' click={ (e) => setValue(e, props.calculatorId) } gridClass='calc-button-grid-item-zero' />
            <CalcButton value='.' click={ (e) => setValue(e, props.calculatorId) }  />
            <CalcButton value='=' click={ (e) => setValue(e, props.calculatorId) }  />

        </div>
    )
}


export default calculator;
